import React, { Component } from 'react'

export default class Get_InputValue extends Component {

    state = {
        name: ""
    }

    onChangeHandler = e => {
        let getname = e.target.value
        this.setState({
            name: getname
        })
    }

    render() {
        return (
            <div className="border border-secondary m-2 pb-2">
                <form>
                <p>What is happening!</p>
                <p>{this.state.name}</p>
                <input onChange={this.onChangeHandler.bind(this)} type="text" placeholder="What Will Be Happening!" className="form-control"/>
                </form>
            </div>
        )
    }
}
